import React, { useState } from 'react';
import Header from './Header';
import GameOver from './GameOver';
import Card from './Card';
import '../style.css';
import axolotl from '../images/axolotl.png';
import cow from '../images/cow.png';
import creeper from '../images/creeper.png';
import enderman from '../images/enderman.png';
import fox from '../images/fox.jpg';
import pig from '../images/pig.png';
import sheep from '../images/sheep.png';
import skeleton from '../images/skeleton.png';
import slime from '../images/slime.png';
import spider from '../images/spider.jpg';
import steve from '../images/steve.png';
import villager from '../images/villager.png';

const GameBoard = () => {
    const [score, setScore] = useState(0);
    const [highScore, setHighScore] = useState(0);
    const [gameOver, endGame] = useState(false);

    let initDeck = [
        <Card takeTurn={takeTurn}
            image={axolotl}
            text='Axolotl'
        />,
        <Card takeTurn={takeTurn}
        image={cow}
        text='Cow'
        />,
        <Card takeTurn={takeTurn}
        image={creeper}
        text='Creeper'
        />,
        <Card takeTurn={takeTurn}
        image={enderman}
        text='Enderman'
        />,
        <Card takeTurn={takeTurn}
        image={fox}
        text='Fox'
        />,
        <Card takeTurn={takeTurn}
        image={pig}
        text='Pig'
        />,
        <Card takeTurn={takeTurn}
        image={sheep}
        text='Sheep'
        />,
        <Card takeTurn={takeTurn}
        image={skeleton}
        text='Skeleton'
        />,
        <Card takeTurn={takeTurn}
        image={slime}
        text='Slime'
        />,
        <Card takeTurn={takeTurn}
        image={spider}
        text='Spider'
        />,
        <Card takeTurn={takeTurn}
        image={steve}
        text='Steve'
        />,
        <Card takeTurn={takeTurn}
        image={villager}
        text='Villager'
        />,
    ];

    const [deck, updateDeck] = useState(initDeck);

    function shuffle(array){
        for (let i = array.length - 1; i > 0; i--) {
          const j = Math.floor(Math.random() * (i + 1));
          const temp = array[i];
          array[i] = array[j];
          array[j] = temp;
        }
        return array;
    }

    function takeTurn(valid){
        console.log('pressed! ' + score + valid);

        // End game
        if(!valid){
            alert('game over');
            endGame(true);
        }

        // Update score
        setScore(score++);
        if(score > highScore){
            setHighScore(score);
        }

        // Shuffle deck
        updateDeck(shuffle(deck));
    }

    return (
        <div className='game-board'>
            <img src='../images/cow.png' alt="alt" />
            <Header score={score} highScore={highScore} />
            {!gameOver ? deck : <GameOver score={score} highScore={highScore} />}
        </div>
    );

}

export default GameBoard;
