import React, { useState } from 'react';
import Header from './Header';
import GameOver from './GameOver';
import Card from './Card';

const GameBoard = () => {
    const [score, setScore] = useState(0);
    const [highScore, setHighScore] = useState(0);
    const [gameOver, endGame] = useState(false);

    let initDeck = [
        <Card takeTurn={takeTurn}
            image='../images/axolotl.png'
            text='Axolotl'
        />,
        <Card takeTurn={takeTurn}
        image='../images/cow.png'
        text='Cow'
        />,
        <Card takeTurn={takeTurn}
        image='../images/creeper.png'
        text='Creeper'
        />,
        <Card takeTurn={takeTurn}
        image='../images/enderman.png'
        text='Enderman'
        />,
        <Card takeTurn={takeTurn}
        image='../images/fox.jpg'
        text='Fox'
        />,
        <Card takeTurn={takeTurn}
        image='../images/pig.png'
        text='Pig'
        />,
        <Card takeTurn={takeTurn}
        image='../images/sheep.png'
        text='Sheep'
        />,
        <Card takeTurn={takeTurn}
        image='../images/skeleton.png'
        text='Skeleton'
        />,
        <Card takeTurn={takeTurn}
        image='../images/slime.png'
        text='Slime'
        />,
        <Card takeTurn={takeTurn}
        image='../images/spider.jpg'
        text='Spider'
        />,
        <Card takeTurn={takeTurn}
        image='../images/steve.png'
        text='Steve'
        />,
        <Card takeTurn={takeTurn}
        image='../images/villager.png'
        text='Villager'
        />,
    ];

    const [deck, updateDeck] = useState([initDeck]);

    function shuffle(array){
        for (let i = array.length - 1; i > 0; i--) {
          const j = Math.floor(Math.random() * (i + 1));
          const temp = array[i];
          array[i] = array[j];
          array[j] = temp;
        }
        return array;
    }

    function takeTurn(valid){
        console.log('pressed! ' + score + valid);

        // End game
        if(!valid){
            alert('game over');
            endGame(true);
        }

        // Update score
        setScore(score++);
        if(score > highScore){
            setHighScore(score);
        }

        // Shuffle deck
        updateDeck(shuffle(deck));
    }

    return (
        <div className='game-board'>
            <Header score={score} highScore={highScore} />
            {!gameOver ? deck : <GameOver score={score} highScore={highScore} />}
        </div>
    );

}

export default GameBoard;
