import React, { useEffect, useState } from 'react';
import Header from './Header';
import GameOver from './GameOver';
import Card from './Card';
import { data } from './data';
import '../style.css';

const GameBoard = () => {
    const [score, setScore] = useState(0);
    const [highScore, setHighScore] = useState(0);
    const [gameOver, endGame] = useState(false);

    /*
    let initDeck = [
        <Card takeTurn={takeTurn}
            image={axolotl}
            text='Axolotl'
            key={0}
        />,
        <Card takeTurn={takeTurn}
            image={cow}
            text='Cow'
            key={1}
        />,
        <Card takeTurn={takeTurn}
            image={creeper}
            text='Creeper'
            key={2}
        />,
        <Card takeTurn={takeTurn}
            image={enderman}
            text='Enderman'
            key={3}
        />,
        <Card takeTurn={takeTurn}
            image={fox}
            text='Fox'
            key={4}
        />,
        <Card takeTurn={takeTurn}
            image={pig}
            text='Pig'
            key={5}
        />,
        <Card takeTurn={takeTurn}
            image={sheep}
            text='Sheep'
            key={6}
        />,
        <Card takeTurn={takeTurn}
            image={skeleton}
            text='Skeleton'
            key={7}
        />,
        <Card takeTurn={takeTurn}
            image={slime}
            text='Slime'
            key={8}
        />,
        <Card takeTurn={takeTurn}
            image={spider}
            text='Spider'
            key={9}
        />,
        <Card takeTurn={takeTurn}
            image={steve}
            text='Steve'
            key={10}
        />,
        <Card takeTurn={takeTurn}
            image={villager}
            text='Villager'
            key={11}
        />
    ];
    */



    const [deck, updateDeck] = useState(initDeck);

    function shuffle(array){
        for (let i = array.length - 1; i > 0; i--) {
          const j = Math.floor(Math.random() * (i + 1));
          const temp = array[i];
          array[i] = array[j];
          array[j] = temp;
        }
        return array;
    }

    function takeTurn(valid){
        // End game
        if(!valid){
            endGame(true);
        }

        // Update score
        setScore(score + 1);
        if(score > highScore){
            setHighScore(score => score);
        }
        // Shuffle deck
        updateDeck(shuffle(deck));
    }

    return (
        <div className='game-board'>
            <Header score={score} highScore={highScore} />
            {!gameOver ?
                <div className='deck'>{deck.map((card) => {
                    return <div className='card-container'>{card}</div>;
                })}</div> 
            : 
                <GameOver score={score} highScore={highScore} 
            />}
        </div>
    );

}

export default GameBoard;
