import React, { useState } from "react";

const Card = ({image='../images/cow.png', text, takeTurn}) => {
    const [selectedStatus, updateSelected] = useState(false);

    const clickHandler = () => {
        takeTurn(selectedStatus);
        updateSelected(true);
    }

    return (
        <button className="Card" onClick={clickHandler}>
            <img src={image} alt="" />
            <div>{text}</div>
        </button>
    );
}

export default Card;
